mapper.select:
SELECT CAR_CODE, DATE_FORMAT(REG_DT,'%Y-%m-%d %T') AS REG_DT FROM CAR WHERE 1 = 1
AND CAR_CODE LIKE CONCAT('%',#{keyword},'%')
AND CAR_CODE = #{carCode}


SELECT B.MON, B.CNT FROM CAR A
INNER JOIN CAR_MON_CNT  B ON A.CAR_CODE =B.CAR_CODE 
WHERE B.MON =#{mon}

	AND B.CAR_CODE = #{carCode}

mapper.insert:
UPDATE CAR_MON_CNT SET    		
	CNT=CNT+#{cnt}
WHERE CAR_CODE=#{carCode}
AND MON=#{mon}

    	delete from CAR_MON_CNT
    	where MON=#{mon}

			AND CAR_CODE=#{carCode}

mapper.update:
update item set    		 
	item_nm=#{itemNm}
	,item_kind=#{itemKind}
	,oem_price=#{oemPrice}
	,as_price=#{asPrice}		
where item_no=#{itemNo}

mapper.select:
select a.ITEM_NO ,b.ITEM_NM ,a.MON ,a.CNT ,a.REG_DT FROM ITEM_MON_RCAR A LEFT OUTER JOIN ITEM B ON A.ITEM_NO=B.ITEM_NO WHERE MON=#{mon}
AND A.ITEM_NO=#{itemNo}
ORDER BY A.ITEM_NO

mapper.insert:
INSERT INTO ITEM_MON_RCAR(
	ITEM_NO    		   				
	, MON
	, CNT
	,REG_DT
)VALUES(
	#{itemNo}    		
	,#{mon}    		
	,#{cnt}
	,NOW()
)

