mapper.update:
UPDATE CAR SET    		
	CAR_NM=#{carNm}    		
	,UPDATE_DT=NOW()
WHERE CAR_CODE=#{carCode}

mapper.select:

SELECT A.CAR_CODE, B.CAR_NM, A.MON, A.CNT 
FROM CAR_MON_CNT A
LEFT OUTER JOIN CAR B ON A.CAR_CODE =B.CAR_CODE 
WHERE A.MON =#{mon}

	AND A.CAR_CODE = #{carCode}

mapper.update:
UPDATE CAR_MON_CNT SET    		
	CNT=CNT+#{cnt} 
WHERE CAR_CODE=#{carCode}
AND MON=#{mon}

update item set    		 
	item_nm=#{itemNm}
	,item_kind=#{itemKind}
	,oem_price=#{oemPrice}
	,as_price=#{asPrice}		
where item_no=#{itemNo}

mapper.select:
select A.ITEM_NO ,B.ITEM_NM ,A.MON ,A.CNT ,A.REG_DT FROM ITEM_MON_RCAR A LEFT OUTER JOIN ITEM B ON A.ITEM_NO=B.ITEM_NO WHERE MON=#{mon}
AND A.ITEM_NO=#{itemNo}
ORDER BY A.ITEM_NO

mapper.insert:
INSERT INTO ITEM_MON_RCAR(
	ITEM_NO    		   				
	, MON
	, CNT
	,REG_DT
)VALUES(
	#{itemNo}    		
	,#{mon}    		
	,#{cnt}
	,NOW()
)

