mapper.select:
select A.MON, A.ITEM_NO, B.ITEM_NM, A.SUM_CNT, A.REG_DT FROM ITEM_RCAR_SUM A INNER JOIN ITEM B ON A.ITEM_NO=B.ITEM_NO WHERE MON=#{mon}
AND A.ITEM_NO=#{itemNo}
ORDER BY A.ITEM_NO

select A.MON, A.ITEM_NO, B.ITEM_NM, A.SUM_CNT, A.REG_DT FROM ITEM_CKD_SUM A INNER JOIN ITEM B ON A.ITEM_NO=B.ITEM_NO WHERE MON=#{mon}
AND A.ITEM_NO=#{itemNo}
ORDER BY A.ITEM_NO

select A.MON, A.ITEM_NO, B.ITEM_NM, A.SUM_CNT, A.REG_DT FROM ITEM_AS_SUM A INNER JOIN ITEM B ON A.ITEM_NO=B.ITEM_NO WHERE MON=#{mon}
AND A.ITEM_NO=#{itemNo}
ORDER BY A.ITEM_NO

select A.ITEM_NO ,B.ITEM_NM ,A.MON ,A.CNT ,A.REG_DT FROM ITEM_MON_MOBIS A LEFT OUTER JOIN ITEM B ON A.ITEM_NO=B.ITEM_NO WHERE A.MON=#{mon}
AND A.ITEM_NO=#{itemNo}
